import { bindActionCreators, createStore } from 'redux';
const initState = { counter: 0, showCounter: true };

const counterReducer = (state = initState, dispatchAction) => {
	if (dispatchAction.type === 'increment') {
		return { counter: state.counter + 1, showCounter: state.showCounter };
	}

	if (dispatchAction.type === 'increase') {
		return {
			counter: state.counter + dispatchAction.amount,
			showCounter: state.showCounter,
		};
	}

	if (dispatchAction.type === 'decrement') {
		return { counter: state.counter - 1, showCounter: state.showCounter };
	}

	if (dispatchAction.type === 'decrease') {
		return {
			counter: state.counter - dispatchAction.amount,
			showCounter: state.showCounter,
		};
	}

	if (dispatchAction.type === 'toggle') {
		return { counter: state.counter, showCounter: !state.showCounter };
	}

	return state;
};

const store = createStore(counterReducer);

export default store;
